#include <iostream>

int main() {
    int a = 10;
    int b = 5;
    std::cout << "Sum: " << (a + b) << std::endl;
    std::cout << "Difference: " << (a - b) << std::endl;
    return 0;
}

#include <iostream>

int main() {
    int number = 10;
    if (number % 2 == 0) {
        std::cout << "Even number" << std::endl;
    } else {
        std::cout << "Odd number" << std::endl;
    }
    return 0;
}
