void swapNums(int &x, int &y) {
    int z = x;
    x = y;
    y = z;
}

int main() {
    int firstNum = 10;
    int secondNum = 20;
    cout << "Before swap: " << "\n";
    cout << firstNum << " " << secondNum << "\n";

    // Call the function, which will change the values of firstNum and secondNum
    swapNums(firstNum, secondNum);
    cout << "After swap: " << "\n";
    cout << firstNum << " " << secondNum << "\n";

    return 0;
}

void modifyStr(string &str) {
    str += " World!";
}

int main() {
    string greeting = "Hello";
    modifyStr(greeting);
    cout << greeting;
    return 0;
}
