#include <iostream>
#include <cmath>

int main() {
    double number = 16.0;
    double squareRoot = sqrt(number);
    double power = pow(2, 3);

    std::cout << "Square root of " << number << " is " << squareRoot << std::endl;
    std::cout << "2 raised to the power of 3 is " << power << std::endl;

    return 0;
}
