Writing to a File

#include <iostream>
#include <fstream>
using namespace std;

int main() {
  ofstream myfile("example.txt");
  if (myfile.is_open()) {
    myfile << "Hello, this is a test file.";
    myfile.close();
    cout << "File written successfully." << endl;
  } else {
    cout << "Unable to open file." << endl;
  }
  return 0;
}

Reading from a File

#include <iostream>
#include <fstream>
using namespace std;

int main() {
  string line;
  ifstream myfile("example.txt");
  if (myfile.is_open()) {
    while (getline(myfile, line)) {
      cout << line << endl;
    }
    myfile.close();
  } else {
    cout << "Unable to open file." << endl;
  }
  return 0;
}

Reading and Writing using fstream

#include <iostream>
#include <fstream>
using namespace std;

int main() {
  fstream file;
  file.open("data.txt", ios::out | ios::in | ios::app);
  if (file.is_open()) {
    file << "Appending some data to the file." << endl;
    file.seekg(0);
    string line;
    while (getline(file, line)) {
      cout << line << endl;
    }
    file.close();
  } else {
    cout << "Unable to open file." << endl;
  }
  return 0;
}
